package Conditional;

public class NestedIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Day=8;
		 if(Day==1)
		 {
			 System.out.println("Monday");
		 }
		 
		 else if(Day==2)
		 {
			 System.out.println("Tuesday");
		 }
		 
		 else if(Day==3)
		 {
			 System.out.println("Wednesday");
		 }
		 
		 else {
			 System.out.println("One day");
		 }


	}

}
